import React, { useState } from "react";
import axios from "axios";

function Add_Doctors() {
  const [formvalue, setFormvalue] = useState({
    id: "",
    name: "",
    Specialty: "",
    Hospital: "",
    contact: "",
    email: "",
  });

  const getform = (e) => {
    setFormvalue({
      ...formvalue,
      id: new Date().getTime().toString(),
      [e.target.name]: e.target.value,
    });
    console.log(formvalue);
  };

  const submithandel = async (e) => {
    e.preventDefault(); // stop page reload
    const res = await axios.post(`http://localhost:3000/categories`, formvalue);
    //console.log(res);
    if (res.status == 201) {
      setFormvalue({ ...formvalue, cate_name: "", cate_img: "" });
      alert("Categories submited success");
      return false;
    }
  };
  return (
    <>
      <div className="container mt-5">
        <h2>Add Doctor</h2>
        <form action="" method="post" onSubmit={submithandel}>
          <div className="form-group">
            <label htmlFor="name">Name:</label>
            <input
              type="text"
              className="form-control"
              id="name"
              placeholder="Enter name"
              required
              name="name"
              value={formvalue.name}
              onChange={getform}
            />
          </div>
          <div className="form-group">
            <label htmlFor="specialty">Specialty:</label>
            <input
              type="text"
              className="form-control"
              id="specialty"
              placeholder="Enter specialty"
              required
              name="Specialty"
              value={formvalue.Specialty}
              onChange={getform}
            />
          </div>
          <div className="form-group">
            <label htmlFor="hospital">Hospital:</label>
            <input
              type="text"
              className="form-control"
              id="hospital"
              placeholder="Enter hospital"
              required
              name="Hospital"
              value={formvalue.Hospital}
              onChange={getform}
            />
          </div>
          <div className="form-group">
            <label htmlFor="contact">Contact:</label>
            <input
              type="tel"
              className="form-control"
              id="contact"
              placeholder="Enter contact number"
              required
              name="contact"
              value={formvalue.contact}
              onChange={getform}
            />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email:</label>
            <input
              type="email"
              className="form-control"
              id="email"
              placeholder="Enter email"
              required
              name="email"
              value={formvalue.email}
              onChange={getform}
            />
          </div>
          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </form>
      </div>
    </>
  );
}

export default Add_Doctors;
